package com.kson.flowlayout;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.TextView;import android.widget.Toast;public class MainActivity extends AppCompatActivity {    private FlowLayout flowLayout;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        flowLayout = findViewById(R.id.flowLayout);        for (int i = 0; i < flowLayout.getChildCount(); i++) {            final TextView tv = (TextView) flowLayout.getChildAt(i);            tv.setOnClickListener(new View.OnClickListener() {                @Override                public void onClick(View v) {                    Toast.makeText(MainActivity.this, ""+tv.getText().toString(), Toast.LENGTH_SHORT).show();                }            });        }    }}